using System;
using FluentAssertions;
using NUnit.Framework;
using Promat.Translations.Constants;
using Promat.Translations.Models;

namespace Promat.Translations.Tests.Unit
{
    public class ClassTranslationShould
    {

        [Test]
        public void CanBeConstructedTranslation()
        {
            var constructorsCalls = new Func<Translation>[]
            {
                    () => new Translation("Hola", Languages.Ingles),
                    () => new Translation("Hola", Languages.Ingles, Languages.Frances),
                    () => new Translation("Hola", Languages.Español, new[] { Languages.Ingles }),
                    () => new Translation("Hola", Languages.Español, new[] { Languages.Ingles, Languages.Frances })
            };

            foreach (var constructorCall in constructorsCalls)
            {
                constructorCall.Should().NotThrow();
            }
        }

