        [Test]
        public void CanNotBeConstructedWithNullOrEmptyFromAndToLanguages()
        {
            var constructorsCalls = new Func<Translation>[]
            {
                    () => new Translation("Hola"),
                    () => new Translation("Hola", null),
                    () => new Translation("Hola", new Languages[] { }),
                    () => new Translation("Hola", Languages.Ingles, null),
                    () => new Translation("Hola", Languages.Español, new Languages[] { })
            };
            foreach (var action in constructorsCalls)
            {
                action.Should().Throw<ArgumentNullException>();
            }
        }
    }
